export default function handleCpf(event){
    var campo = event.target;
    var valor = campo.value.replace(/\D/g, ''); // remove tudo que não é número
    valor = valor.replace(/(\d{3})(\d)/, '$1.$2'); // coloca ponto depois dos 3 primeiros números
    valor = valor.replace(/(\d{3})(\d)/, '$1.$2'); // coloca ponto depois dos 6 primeiros números
    valor = valor.replace(/(\d{3})(\d{1,2})$/, '$1-$2'); // coloca hífen antes dos últimos 2 números
    campo.value = valor; 

    console.log(validarCPF(valor))

    if(!validarCPF(valor)){
        campo.classList.add('error');
    }
    else{
        campo.classList.remove('error');
        campo.classList.add('val');


    }
}

function validarCPF(cpf) {
    cpf = cpf.replace(/[^\d]+/g,''); 
    if (cpf == '') return false; 
    var soma = 0;
    for (let i = 0; i < 9; i++) {
      soma += parseInt(cpf.charAt(i)) * (10 - i);
    }
    var resto = 11 - (soma % 11);
    if (resto === 10 || resto === 11) {
      resto = 0;
    }
    if (resto != parseInt(cpf.charAt(9))) {
      return false;
    }
    soma = 0;
    for (let i = 0; i < 10; i++) {
      soma += parseInt(cpf.charAt(i)) * (11 - i);
    }
    resto = 11 - (soma % 11);
    if (resto === 10 || resto === 11) {
      resto = 0;
    }
    if (resto != parseInt(cpf.charAt(10))) {
      return false;
    }
  
    return true;
  }
