import styled from "styled-components"

const Input = styled.input`
    width:80vw;
    height: 1.3rem;
    background-color: #00000040;
    border:1px solid #ffffff40;
    border-radius:.2rem;
    color:#fff;
`
const fullInputs = ({ title, value, onChange }) => {
    const handleInputChange = (event) => {
        const { name, value } = event.target;

        // Valida a máscara com base no título
        if (title === 'email') {
            // Valida o email
            // Aqui você pode usar uma expressão regular ou uma biblioteca de validação de email
            if (!/\S+@\S+\.\S+/.test(value)) {
                return;
            }
        } else if (title === 'cpf') {
            // Valida o CPF
            // Aqui você pode usar uma biblioteca de validação de CPF
            if (value.length !== 11) {
                return;
            }
        } else if (title === 'telefone') {
            // Valida o telefone
            // Aqui você pode usar uma biblioteca de validação de telefone
            if (value.length !== 10 && value.length !== 11) {
                return;
            }
        }

        onChange(event);
    };

    return (<>
    <Input name={title} value={value} onChange={handleInputChange} />
    </>);
};




export default fullInput;
