export function formatarCPF(event) {
    var campo = event.target;
    var valor = campo.value.replace(/\D/g, ''); // remove tudo que não é número
    valor = valor.substring(0, 11); // limita a entrada a 11 dígitos
    valor = valor.replace(/(\d{3})(\d)/, '$1.$2'); // coloca ponto depois dos 3 primeiros números
    valor = valor.replace(/(\d{3})(\d)/, '$1.$2'); // coloca ponto depois dos 6 primeiros números
    valor = valor.replace(/(\d{3})(\d{1,2})$/, '$1-$2'); // coloca hífen antes dos últimos 2 números
    campo.value = valor; // atualiza o valor do campo com a máscara
  
    // Verifica se o valor digitado é um CPF válido e adiciona ou remove a classe de erro
    var valido = validarCPF(valor);
    if (valido) {
      campo.classList.remove('error');
    } else {
      campo.classList.add('error');
    }
  }
  
  function validarCPF(cpf) {
    cpf = cpf.replace(/[^\d]+/g,''); // remove tudo que não é número
    if (cpf == '') return false; // se o CPF estiver vazio, é inválido
  
    // Valida o primeiro dígito verificador do CPF
    var soma = 0;
    for (var i = 0; i < 9; i++) {
      soma += parseInt(cpf.charAt(i)) * (10 - i);
    }
    var resto = 11 - (soma % 11);
    if (resto == 10 || resto == 11) {
      resto = 0;
    }
    if (resto != parseInt(cpf.charAt(9))) {
      return false;
    }
  
    // Valida o segundo dígito verificador do CPF
    soma = 0;
    for (var i = 0; i < 10; i++) {
      soma += parseInt(cpf.charAt(i)) * (11 - i);
    }
    resto = 11 - (soma % 11);
    if (resto == 10 || resto == 11) {
      resto = 0;
    }
    if (resto != parseInt(cpf.charAt(10))) {
      return false;
    }
  
    return true;
  }
