export default function handleEmail(event){
    if(/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(event.target.value)){
        event.target.classList.remove('error');
        camp.classList.add('valid');
    }
    else{
        campo.classList.add('error');
        campo.classList.remove('valid');
    }
}
