function formataCpf(cpf) {
    if (!cpf) return cpf; // retorna o valor original se for vazio ou nulo
    const formattedCpf = cpf.replace(/\D/g, '').replace(/^(\d{3})(\d{3})(\d{3})(\d{2})$/, '$1.$2.$3-$4');
    return formattedCpf;
  }
  
  export default function handleCpf(event) {
    const rawInput = event.target.value;
    const formattedInput = formataCpf(rawInput);
    event.target.value = formattedInput;
  }
