function formataCpf(cpf){
    cpf = cpf.replace(/[^\d]/g, '');
    cpf = cpf.replace(/(\d{3})(\d)/, '$1.$2');
    cpf = cpf.replace(/(\d{3})(\d)/, '$1.$2');
    cpf = cpf.replace(/(\d{3})(\d{1,2})$/, '$1-$2');
    return cpf;
}

export default function handleCpf(event){
    const rawInput = formataCpf(event.target.value);
    event.target.value = rawInput;
}
